ORDER BY PRTCPT_DVC_ID;


SELECT PRTCPT_DVC_INTERNAL_ID
FROM ROADMAP.PRTCPT_DVC
GROUP BY PRTCPT_DVC_INTERNAL_ID
HAVING COUNT(DISTINCT PRTCPT_DVC_ID) > 1
ORDER BY PRTCPT_DVC_INTERNAL_ID;

SELECT 
    STUDY_PRTCPT_ID    ,
    COUNT(DISTINCT PRTCPT_DVC_INTERNAL_ID) AS DISTINCT_INTERNAL_ID_COUNT
FROM 
    ROADMAP.PRTCPT_DVC
GROUP BY 
    STUDY_PRTCPT_ID    
HAVING 
    COUNT(DISTINCT PRTCPT_DVC_INTERNAL_ID) > 1
ORDER BY 
    DISTINCT_INTERNAL_ID_COUNT DESC;


    
SELECT 
    PRTCPT_DVC_ID,
    LISTAGG(PRTCPT_DVC_INTERNAL_ID, ', ') WITHIN GROUP (ORDER BY PRTCPT_DVC_INTERNAL_ID) AS INTERNAL_IDS
FROM 
    ROADMAP.PRTCPT_DVC
GROUP BY 
    PRTCPT_DVC_ID
HAVING 
    COUNT(DISTINCT PRTCPT_DVC_INTERNAL_ID) > 1
ORDER BY 
    PRTCPT_DVC_ID;    
    
    
WITH multiple_participant_devices AS (
    SELECT 
        PRTCPT_DVC_ID,
        COUNT(DISTINCT STUDY_PRTCPT_ID) AS DISTINCT_PARTICIPANT_COUNT
    FROM 
        ROADMAP.PRTCPT_DVC
    GROUP BY 
        PRTCPT_DVC_ID
    HAVING 
        COUNT(DISTINCT STUDY_PRTCPT_ID) > 1
)
SELECT 
    pd.PRTCPT_DVC_ID,
    mpd.DISTINCT_PARTICIPANT_COUNT,
    pd.STUDY_PRTCPT_ID,
    pd.STUDY_ID,
    pd.PRTCPT_DVC_INTERNAL_ID,
    pd.PRTCPT_DVC_MAKE,
    pd.PRTCPT_DVC_MODEL
FROM 
    ROADMAP.PRTCPT_DVC pd
JOIN 
    multiple_participant_devices mpd ON pd.PRTCPT_DVC_ID = mpd.PRTCPT_DVC_ID
ORDER BY 
    pd.PRTCPT_DVC_ID, pd.STUDY_PRTCPT_ID;    